/**
 * Knowledge Graph Visualization Component
 * 
 * Uses react-force-graph-2d for interactive graph rendering.
 * Color-coded by node type: Company, Person, Location, Product, Technology
 */

import React, { useRef, useCallback, useMemo } from 'react';
import ForceGraph2D from 'react-force-graph-2d';

// Color scheme for node types
const NODE_COLORS = {
  Company: '#7b5cff',      // Purple - central entity
  Person: '#00d4aa',       // Teal - people
  Location: '#ff6b9d',     // Pink - locations
  Technology: '#ffd93d',   // Yellow - tech stack
  Product: '#4ecdc4',      // Cyan - products/services
};

const KnowledgeGraph = ({ graph, width = 600, height = 400 }) => {
  const graphRef = useRef();
  
  // Transform graph data for force-graph
  const graphData = useMemo(() => {
    if (!graph || !graph.nodes) {
      return { nodes: [], links: [] };
    }
    
    return {
      nodes: graph.nodes.map(node => ({
        id: node.id,
        label: node.label,
        type: node.type,
        properties: node.properties,
        color: NODE_COLORS[node.type] || '#888888',
        // Size based on type
        size: node.type === 'Company' ? 20 : 12,
      })),
      links: graph.edges.map(edge => ({
        source: edge.source,
        target: edge.target,
        relationship: edge.relationship,
      })),
    };
  }, [graph]);
  
  // Custom node rendering
  const nodeCanvasObject = useCallback((node, ctx, globalScale) => {
    const label = node.label;
    const fontSize = Math.max(10 / globalScale, 3);
    const nodeSize = node.size / globalScale;
    
    // Draw node circle
    ctx.beginPath();
    ctx.arc(node.x, node.y, nodeSize, 0, 2 * Math.PI);
    ctx.fillStyle = node.color;
    ctx.fill();
    
    // Draw border for company node
    if (node.type === 'Company') {
      ctx.strokeStyle = '#ffffff';
      ctx.lineWidth = 2 / globalScale;
      ctx.stroke();
    }
    
    // Draw label
    ctx.font = `${fontSize}px 'Outfit', sans-serif`;
    ctx.textAlign = 'center';
    ctx.textBaseline = 'top';
    ctx.fillStyle = '#f0f0f5';
    
    // Truncate long labels
    const maxLength = 15;
    const displayLabel = label.length > maxLength 
      ? label.substring(0, maxLength) + '...' 
      : label;
    
    ctx.fillText(displayLabel, node.x, node.y + nodeSize + 2);
  }, []);
  
  // Link rendering
  const linkCanvasObject = useCallback((link, ctx, globalScale) => {
    const start = link.source;
    const end = link.target;
    
    if (!start.x || !end.x) return;
    
    // Draw line
    ctx.beginPath();
    ctx.moveTo(start.x, start.y);
    ctx.lineTo(end.x, end.y);
    ctx.strokeStyle = 'rgba(136, 136, 160, 0.4)';
    ctx.lineWidth = 1 / globalScale;
    ctx.stroke();
  }, []);
  
  // Node tooltip
  const getNodeTooltip = useCallback((node) => {
    if (!node) return '';
    let tooltip = `${node.type}: ${node.label}`;
    if (node.properties) {
      if (node.properties.title) {
        tooltip += `\nTitle: ${node.properties.title}`;
      }
      if (node.properties.category) {
        tooltip += `\nCategory: ${node.properties.category}`;
      }
    }
    return tooltip;
  }, []);
  
  // Handle node click - zoom to node
  const handleNodeClick = useCallback((node) => {
    if (graphRef.current) {
      graphRef.current.centerAt(node.x, node.y, 500);
      graphRef.current.zoom(2, 500);
    }
  }, []);
  
  if (!graph || !graph.nodes || graph.nodes.length === 0) {
    return (
      <div className="graph-empty">
        <p>No graph data available</p>
      </div>
    );
  }
  
  return (
    <div className="knowledge-graph-container">
      <div className="graph-legend">
        {Object.entries(NODE_COLORS).map(([type, color]) => (
          <div key={type} className="legend-item">
            <span 
              className="legend-dot" 
              style={{ backgroundColor: color }}
            />
            <span className="legend-label">{type}</span>
          </div>
        ))}
      </div>
      
      <div className="graph-canvas">
        <ForceGraph2D
          ref={graphRef}
          graphData={graphData}
          width={width}
          height={height}
          backgroundColor="#12121a"
          nodeCanvasObject={nodeCanvasObject}
          linkCanvasObject={linkCanvasObject}
          nodeLabel={getNodeTooltip}
          onNodeClick={handleNodeClick}
          enableZoomInteraction={true}
          enablePanInteraction={true}
          cooldownTicks={100}
          d3AlphaDecay={0.02}
          d3VelocityDecay={0.3}
          linkDirectionalParticles={1}
          linkDirectionalParticleSpeed={0.005}
          linkDirectionalParticleWidth={2}
        />
      </div>
      
      <div className="graph-controls">
        <button 
          className="graph-btn"
          onClick={() => graphRef.current?.zoomToFit(400, 50)}
        >
          Fit View
        </button>
        <button 
          className="graph-btn"
          onClick={() => graphRef.current?.centerAt(0, 0, 400)}
        >
          Center
        </button>
      </div>
    </div>
  );
};

export default KnowledgeGraph;
