/**
 * Autonomous Company Intelligence Agent - Frontend
 * 
 * INNOVATION: The agent REFUSES to generate insights when data confidence
 * is low, preventing hallucinated business intelligence.
 * 
 * Features:
 * - Professional summary card with logo
 * - Visual knowledge graph
 * - Data quality score display
 * - Instant cached responses
 */

import React, { useState, useEffect, useCallback } from 'react';
import KnowledgeGraph from './KnowledgeGraph';
import './App.css';

const API_BASE = 'http://localhost:8000';

// Data Quality level colors
const QUALITY_COLORS = {
  high: '#00d4aa',
  medium: '#ffd93d',
  low: '#ff5c5c',
};

function App() {
  const [companies, setCompanies] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [serverStatus, setServerStatus] = useState('checking');
  const [showRawGraph, setShowRawGraph] = useState(false);
  const [graphDimensions, setGraphDimensions] = useState({ width: 600, height: 400 });

  useEffect(() => {
    checkServer();
    fetchCompanies();
    
    // Responsive graph sizing
    const updateDimensions = () => {
      const width = Math.min(window.innerWidth - 80, 800);
      const height = Math.min(400, width * 0.6);
      setGraphDimensions({ width, height });
    };
    
    updateDimensions();
    window.addEventListener('resize', updateDimensions);
    return () => window.removeEventListener('resize', updateDimensions);
  }, []);

  const checkServer = async () => {
    try {
      const res = await fetch(`${API_BASE}/`);
      const data = await res.json();
      setServerStatus(data.llm_loaded ? 'ready' : 'loading');
    } catch {
      setServerStatus('offline');
    }
  };

  const fetchCompanies = async () => {
    try {
      const res = await fetch(`${API_BASE}/companies`);
      const data = await res.json();
      setCompanies(data.companies || []);
      if (data.companies?.length > 0) {
        setSelectedCompany(data.companies[0].name || data.companies[0]);
      }
    } catch {
      setCompanies([]);
    }
  };

  const processCompany = useCallback(async (forceRefresh = false) => {
    if (!selectedCompany) return;
    
    setLoading(true);
    setError(null);

    try {
      const url = forceRefresh 
        ? `${API_BASE}/process/${selectedCompany}?force_refresh=true`
        : `${API_BASE}/process/${selectedCompany}`;
      
      const res = await fetch(url);
      if (!res.ok) {
        const errData = await res.json();
        throw new Error(errData.detail || 'Processing failed');
      }
      const data = await res.json();
      setResult(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, [selectedCompany]);

  const getCompanyName = (company) => {
    return typeof company === 'object' ? company.name : company;
  };

  const isCached = (company) => {
    return typeof company === 'object' ? company.cached : false;
  };

  const quality = result?.profile?.data_quality;

  return (
    <div className="app">
      <div className="background-pattern"></div>
      
      <header className="header">
        <div className="logo">
          <span className="logo-icon">‚óà</span>
          <div className="logo-text">
            <h1>Company Intelligence Agent</h1>
            <span className="logo-subtitle">Autonomous ‚Ä¢ Offline ‚Ä¢ Privacy-First</span>
          </div>
        </div>
        <div className={`status status-${serverStatus}`}>
          <span className="status-dot"></span>
          {serverStatus === 'ready' && 'LLM Ready'}
          {serverStatus === 'loading' && 'LLM Loading...'}
          {serverStatus === 'offline' && 'Server Offline'}
          {serverStatus === 'checking' && 'Checking...'}
        </div>
      </header>

      <main className="main">
        {/* Control Panel */}
        <section className="control-panel">
          <div className="input-group">
            <label>Select Company</label>
            <select 
              value={selectedCompany} 
              onChange={(e) => setSelectedCompany(e.target.value)}
              disabled={loading || companies.length === 0}
            >
              {companies.length === 0 && <option>No companies found</option>}
              {companies.map(c => (
                <option key={getCompanyName(c)} value={getCompanyName(c)}>
                  {getCompanyName(c)} {isCached(c) ? '‚ö°' : ''}
                </option>
              ))}
            </select>
          </div>
          
          <div className="button-group">
            <button 
              className="process-btn"
              onClick={() => processCompany(false)}
              disabled={loading || !selectedCompany || serverStatus !== 'ready'}
            >
              {loading ? (
                <>
                  <span className="spinner"></span>
                  Processing...
                </>
              ) : (
                <>
                  <span className="btn-icon">‚ö°</span>
                  Extract Intelligence
                </>
              )}
            </button>
            
            {result?.cached && (
              <button 
                className="refresh-btn"
                onClick={() => processCompany(true)}
                disabled={loading}
                title="Force refresh (bypass cache)"
              >
                ‚Üª Refresh
              </button>
            )}
          </div>

          {error && (
            <div className="error-box">
              <span className="error-icon">‚úï</span>
              {error}
            </div>
          )}
        </section>

        {/* Loading State */}
        {loading && (
          <div className="loading-overlay">
            <div className="loading-content">
              <div className="loading-spinner"></div>
              <h3>Extracting Intelligence</h3>
              <p>Analyzing website content with local LLM...</p>
              <p className="loading-note">First run may take 1-3 minutes on CPU</p>
            </div>
          </div>
        )}

        {result && (
          <div className="results">
            {/* Cache & Performance Badge */}
            {(result.cached || result.processing_time_ms !== null) && (
              <div className="performance-badge">
                {result.cached && <span className="badge badge-cached">‚ö° Cached Response</span>}
                {result.processing_time_ms !== null && !result.cached && (
                  <span className="badge badge-time">‚è± {result.processing_time_ms}ms</span>
                )}
              </div>
            )}

            {/* Data Quality Guardian */}
            {quality && (
              <section className={`quality-card quality-${quality.level}`}>
                <div className="quality-header">
                  <h3>
                    <span className="card-icon">üõ°Ô∏è</span>
                    Data Quality Guardian
                  </h3>
                  <div 
                    className="quality-score"
                    style={{ borderColor: QUALITY_COLORS[quality.level] }}
                  >
                    <span className="score-value" style={{ color: QUALITY_COLORS[quality.level] }}>
                      {quality.confidence_score}
                    </span>
                    <span className="score-label">Confidence</span>
                  </div>
                </div>
                
                {!quality.is_reliable && (
                  <div className="quality-warning">
                    <strong>‚ö†Ô∏è LOW CONFIDENCE DATA</strong>
                    <p>
                      The autonomous agent <strong>refuses to generate unreliable business intelligence</strong>.
                      This prevents hallucinated insights from incomplete data.
                    </p>
                  </div>
                )}
                
                {quality.warnings?.length > 0 && quality.is_reliable && (
                  <div className="quality-notes">
                    {quality.warnings.map((w, i) => (
                      <span key={i} className="quality-note">{w}</span>
                    ))}
                  </div>
                )}
                
                {quality.missing_fields?.length > 0 && (
                  <div className="missing-fields">
                    <span className="missing-label">Missing data:</span>
                    {quality.missing_fields.map((f, i) => (
                      <span key={i} className="missing-field">{f}</span>
                    ))}
                  </div>
                )}
              </section>
            )}

            {/* Company Summary Card */}
            <section className="summary-card">
              <div className="summary-header">
                {result.profile.logo_url ? (
                  <div className="company-logo">
                    <img 
                      src={result.profile.logo_url} 
                      alt={`${result.profile.company_name} logo`}
                      onError={(e) => { e.target.style.display = 'none'; }}
                    />
                  </div>
                ) : (
                  <div className="company-logo company-logo-placeholder">
                    {result.profile.company_name?.charAt(0) || '?'}
                  </div>
                )}
                
                <div className="company-info">
                  <h2>{result.profile.company_name || 'Unknown Company'}</h2>
                  {result.profile.industry && (
                    <span className="industry-tag">{result.profile.industry}</span>
                  )}
                  {result.profile.headquarters && (
                    <span className="hq-tag">üìç {result.profile.headquarters}</span>
                  )}
                </div>
              </div>
              
              {result.profile.description_short && (
                <p className="company-description">{result.profile.description_short}</p>
              )}
              
              <div className="summary-grid">
                {/* Products & Services */}
                {result.profile.products_services?.length > 0 && (
                  <div className="summary-section">
                    <h4>
                      <span className="section-icon">‚óÜ</span>
                      Products & Services
                    </h4>
                    <div className="tag-list">
                      {result.profile.products_services.map((item, i) => (
                        <span key={i} className="tag tag-product">{item}</span>
                      ))}
                    </div>
                  </div>
                )}

                {/* Locations */}
                {result.profile.locations?.length > 0 && (
                  <div className="summary-section">
                    <h4>
                      <span className="section-icon">‚óá</span>
                      Locations
                    </h4>
                    <div className="tag-list">
                      {result.profile.locations.map((loc, i) => (
                        <span key={i} className="tag tag-location">{loc}</span>
                      ))}
                    </div>
                  </div>
                )}

                {/* Tech Stack */}
                {result.profile.tech_stack?.length > 0 && (
                  <div className="summary-section">
                    <h4>
                      <span className="section-icon">‚óà</span>
                      Tech Stack
                    </h4>
                    <div className="tag-list">
                      {result.profile.tech_stack.map((tech, i) => (
                        <span key={i} className="tag tag-tech">{tech}</span>
                      ))}
                    </div>
                  </div>
                )}

                {/* Contact */}
                {(result.profile.contact?.email || result.profile.contact?.phone) && (
                  <div className="summary-section">
                    <h4>
                      <span className="section-icon">‚ñ£</span>
                      Contact
                    </h4>
                    <div className="contact-details">
                      {result.profile.contact.email && (
                        <p><span className="contact-label">Email:</span> {result.profile.contact.email}</p>
                      )}
                      {result.profile.contact.phone && (
                        <p><span className="contact-label">Phone:</span> {result.profile.contact.phone}</p>
                      )}
                    </div>
                  </div>
                )}
              </div>
            </section>

            {/* Key People */}
            {result.profile.key_people?.length > 0 && (
              <section className="people-card">
                <h3>
                  <span className="card-icon">‚óâ</span>
                  Key People
                </h3>
                <div className="people-grid">
                  {result.profile.key_people.map((person, i) => (
                    <div key={i} className="person-card">
                      <div className="person-avatar">
                        {person.name?.charAt(0) || '?'}
                      </div>
                      <div className="person-info">
                        <h4>{person.name}</h4>
                        {person.title && <p className="person-title">{person.title}</p>}
                        {person.role_category && (
                          <span className="role-tag">{person.role_category}</span>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              </section>
            )}

            {/* Knowledge Graph */}
            <section className="graph-card">
              <div className="graph-header">
                <h3>
                  <span className="card-icon">‚¨°</span>
                  Knowledge Graph
                </h3>
                <div className="graph-stats">
                  <div className="stat">
                    <span className="stat-value">{result.graph.nodes?.length || 0}</span>
                    <span className="stat-label">Nodes</span>
                  </div>
                  <div className="stat">
                    <span className="stat-value">{result.graph.edges?.length || 0}</span>
                    <span className="stat-label">Edges</span>
                  </div>
                </div>
              </div>
              
              <KnowledgeGraph 
                graph={result.graph} 
                width={graphDimensions.width}
                height={graphDimensions.height}
              />
              
              <div className="graph-actions">
                <button 
                  className="toggle-raw-btn"
                  onClick={() => setShowRawGraph(!showRawGraph)}
                >
                  {showRawGraph ? '‚ñº Hide' : '‚ñ∂ View'} Raw Graph JSON
                </button>
              </div>
              
              {showRawGraph && (
                <div className="raw-graph">
                  <pre>{JSON.stringify(result.graph, null, 2)}</pre>
                </div>
              )}
            </section>
          </div>
        )}
      </main>

      <footer className="footer">
        <div className="footer-content">
          <p className="footer-main">
            <strong>Autonomous Company Intelligence Agent</strong>
          </p>
          <p className="footer-sub">
            Offline LLM (Phi-2) ‚Ä¢ Zero Cloud APIs ‚Ä¢ Full Privacy ‚Ä¢ No Data Leaves Your Machine
          </p>
          <p className="footer-innovation">
            üõ°Ô∏è <em>Innovation: Agent refuses to generate insights when data confidence is low</em>
          </p>
        </div>
      </footer>
    </div>
  );
}

export default App;
